import { Component, OnInit } from "@angular/core";
import { FormControl, FormGroup } from "@angular/forms";

@Component({
  selector: "app-guess-number",
  templateUrl: "./guess-number.component.html",
  styleUrls: ["./guess-number.component.scss"],
})
export class GuessNumberComponent implements OnInit {
  public formGroupEl!: FormGroup;
  public userScore: number = 20;
  public highScore: number = -1;
  public feedback: string = "asd";

  public ngOnInit(): void {
    this.formGroupEl = new FormGroup({
      inputNumber: new FormControl({ value: 0, disabled: false }),
    });
  }

  public onClickGuess() {
    console.log(this.formGroupEl.controls["inputNumber"].value);
  }
}
<h1>Guess The Number ðŸ˜‹</h1>
<hr>
<section class="container">
  <article class="grid-area--left">
    <form [formGroup]="formGroupEl">
      <label for="input-number">Number:</label>
      <input type="number" formControlName="inputNumber" id="input-number">
    </form>
    <button type="button" (click)="onClickGuess()">GUESS</button>
  </article>
  <article class="grid-area--right">
    <h2>{{feedback}}</h2>
    <p>Score: <span>{{userScore}}</span></p>
    <p>High Score: <span>{{highScore}}</span></p>
  </article>
</section>
