import { Component, forwardRef } from "@angular/core";
import { FormControl, FormGroup, NG_VALUE_ACCESSOR } from "@angular/forms";

@Component({
  selector: "app-guess-number",
  templateUrl: "./guess-number.component.html",
  styleUrls: ["./guess-number.component.scss"],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => GuessNumberComponent),
      multi: true,
    },
  ],
})
export class GuessNumberComponent {
  public formGroupEl = new FormGroup({
    inputNumber: new FormControl(null),
  });
  public userScore: number = 20;
  public highScore: number = -1;
  public feedback: string = "";

  public onClickGuess() {
    console.log(this.formGroupEl.controls["inputNumber"].value);
  }
}
<h1>Guess The Number ðŸ˜‹</h1>
<hr>
<section>
  <article>
    <form [formGroup]="formGroupEl">
      <label for="input-number">Number:</label>
      <input type="number" [formControl]="inputNumber" id="input-number">
      <button type="button" (click)="onClickGuess()">GUESS</button>
    </form>
  </article>
  <article>
    <h2 [(ngModel)]="feedback"></h2>
    <p>Score: <span>{{userScore}}</span></p>
    <p>High Score: <span>{{highScore}}</span></p>
  </article>
</section>
